import { MapContainer, TileLayer, useMap, Marker, Popup } from "react-leaflet";

import React, {useEffect, useRef, useState} from "react";
// import './App.css';
import 'leaflet/dist/leaflet.css';
import MapSearch from "./MapSearch";

// const defaultCenter = [0, 0];
// const defaultElement = 4;


import mockData from "./mockData.json";
import { Icon } from "leaflet";


function Karte() {
  // LEAFLET Data
  // Längengradkoordinate muss zwischen -180 und 180 liegen
  const latitude = 52.45727522348639;
  //  Breitengradkoordinate muss zwischen -90 und 90 liegen
  const longitude = 13.540082992085056;
  // WBS CODING SCHOOL as Marker
  const latitudeStart = 52.45727522348639;
  const longitudeStart = 13.540082992085056;

  return (
    <MapContainer
      center={[latitude, longitude]}
      zoom={13}
      scrollWheelZoom={true}
      // zoomControl={false} // no +/-  Button to zoom
    >
      <TileLayer
        attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
      />
      
      {/*       <Marker position={[latitudeStart, longitudeStart]}>
        <Popup>WBS Coding School !</Popup>

      </Marker>
      

      </Marker> */}

      {mockData.boxes.map((box) => (
        <Marker key={box.id} position={[box.longitude, box.latitude]}>
          <Popup position={[box.longitude, box.latitude]}>
            <div>
              <h2>User: {box.user_name} </h2>
              <p>Hier kommt noch das Bild rein</p>
              <p>Tags: ... ... ...</p>
            </div>
          </Popup>
        </Marker>
      ))}

      <MapSearch />
    </MapContainer>
  );
}
export default Karte;
