import Modal from "react-bootstrap/Modal";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";
import React, { useState } from "react";
import { MapContainer, TileLayer, Marker } from "react-leaflet";
import { Icon } from "leaflet";
import markerIconPng from "leaflet/dist/images/marker-icon.png";
import MapSearch from "../MapSearch";

const AddNewBox = ({ showAddNewBox, handleClose, currentUser }) => {
    const [addNewBox, setAddNewBox] = useState({
        boxImagePath: "",
        boxLocationCity: "",
        boxLocationX: "",
        boxLocationY: "",
        books: false,
        cloths: false,
        dishes: false,
        toys: false,
        shoes: false,
        decoration: false,
        gadgets: false,
        tools: false,
        dvd: false,
        videogames: false,
    });

    console.log(addNewBox);

    const handleAddNewBox = (e) => {
        const { name, type, value, checked } = e.target;
        setAddNewBox((prevData) => {
            return {
                ...prevData,
                [name]: type === "checkbox" ? checked : value,
            };
        });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
    };

    const handleAddNewBoxSend = async () => {
        const response = await fetch(
            process.env.REACT_BACKEND_URL + "/box/addbox",
            {
                method: "PUT",
                headers: {
                    "Content-Type": "application/json; charset=utf-8",
                },
                body: JSON.stringify({
                    ...addNewBox,
                    // eslint-disable-next-line no-underscore-dangle
                    currentUser: currentUser._id,
                }),
            },
        );
        const data = await response.json();
        if (data.success) {
            console.log(data.success, data.user);
            handleClose();
        }
    };

    return (
        <Modal
            show={showAddNewBox}
            onHide={handleClose}
            backdrop="true"
            fade="true"
            centered="true"
        >
            <Modal.Header closeButton>
                <Modal.Title>Add a new box</Modal.Title>
            </Modal.Header>
            <Modal.Body>
                <Form
                    action="https://morning-shelf-75082.herokuapp.com/box/addbox"
                    method="PUT"
                    onSubmit={handleSubmit}
                >
                    <Form.Group controlId="formFile" className="mb-3">
                        <Form.Label>Show your box with a picture</Form.Label>
                        <Form.Control type="file" name="imagePath" />
                    </Form.Group>
                    <Form.Group className="mb-3" controlId="formBasicAdress">
                        <Form.Label>Where is your box located?</Form.Label>
                        <div className="form-map-container">
                            <MapContainer
                                center={[52, 13]}
                                zoom={13}
                                zoomControl={false} // no +/-  Button to zoom
                            >
                                <TileLayer
                                    attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
                                    url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                                />
                                <Marker
                                    position={[52, 13]}
                                    icon={
                                        new Icon({
                                            iconUrl: markerIconPng,
                                            iconSize: [25, 41],
                                            iconAnchor: [12, 41],
                                        })
                                    }
                                />
                                <div position="fixed">

                                    <MapSearch  setAddNewBox={setAddNewBox} />
                                </div>
                            </MapContainer>
                        </div>
                    </Form.Group>
                    <Form.Group className="mb-3" controlId="formBasicCheckbox">
                        <Form.Text className="text-muted">
                            What is in your box?
                        </Form.Text>
                        <div className="checkbox-container">
                            <div className="checkbox-left">
                                <Form.Check
                                    type="checkbox"
                                    label="Books"
                                    name="books"
                                    onChange={handleAddNewBox}
                                    checked={addNewBox.books}
                                />
                                <Form.Check
                                    type="checkbox"
                                    label="Cloths"
                                    name="cloths"
                                    onChange={handleAddNewBox}
                                    checked={addNewBox.cloths}
                                />
                                <Form.Check
                                    type="checkbox"
                                    label="Dishes"
                                    name="dishes"
                                    onChange={handleAddNewBox}
                                    checked={addNewBox.dishes}
                                />
                                <Form.Check
                                    type="checkbox"
                                    label="Toys"
                                    name="toys"
                                    onChange={handleAddNewBox}
                                    checked={addNewBox.toys}
                                />
                                <Form.Check
                                    type="checkbox"
                                    label="Shoes"
                                    name="shoes"
                                    onChange={handleAddNewBox}
                                    checked={addNewBox.shoes}
                                />
                            </div>
                            <div className="checkbox-right">
                                <Form.Check
                                    type="checkbox"
                                    label="Decoration"
                                    name="decoration"
                                    onChange={handleAddNewBox}
                                    checked={addNewBox.decoration}
                                />
                                <Form.Check
                                    type="checkbox"
                                    label="Gadgets"
                                    name="gadgets"
                                    onChange={handleAddNewBox}
                                    checked={addNewBox.gadgets}
                                />
                                <Form.Check
                                    type="checkbox"
                                    label="Tools"
                                    name="tools"
                                    onChange={handleAddNewBox}
                                    checked={addNewBox.tools}
                                />
                                <Form.Check
                                    type="checkbox"
                                    label="DVD"
                                    name="dvd"
                                    onChange={handleAddNewBox}
                                    checked={addNewBox.dvd}
                                />
                                <Form.Check
                                    type="checkbox"
                                    label="Videogames"
                                    name="videogames"
                                    onChange={handleAddNewBox}
                                    checked={addNewBox.videogames}
                                />
                            </div>
                        </div>
                    </Form.Group>
                </Form>
            </Modal.Body>
            <Modal.Footer>
                <Button variant="secondary" onClick={handleClose}>
                    Cancel
                </Button>
                <Button variant="primary" onClick={handleAddNewBoxSend}>
                    Add a new box
                </Button>
            </Modal.Footer>
        </Modal>
    );
};

export default AddNewBox;
