import { useEffect } from "react";
import { useMap } from "react-leaflet";
import { OpenStreetMapProvider, GeoSearchControl } from "leaflet-geosearch";
import "leaflet-geosearch/dist/geosearch.css";

export default function MapSearch() {
    const map = useMap();
    const provider = new OpenStreetMapProvider();
    
    const search = new GeoSearchControl({
      provider,
      notFoundMessage: "Sorry, that address could not be found.",
      style: "bar",
      //   style: "button"
    });
    
    const onShowLocation = (result) => {
      if (result) {
        // read location here
        console.log(result);
        const locationInfo = result;
        console.log("x=longitude: " + locationInfo.location.x);
        console.log("y=latitude: " + locationInfo.location.y);
        console.log("label: " + locationInfo.location.label);
      }
    };
    
    useEffect(() => {map.addControl(search);
      map.on("geosearch/showlocation", onShowLocation);
      return () => {
        map.removeControl(search);
        map.off("geosearch/showlocation", onShowLocation);
      };}, [])
    
    return null

}

// https://github.com/smeijer/leaflet-geosearch
