import React from "react";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css"; 
import "slick-carousel/slick/slick-theme.css";
import {Card, Button} from 'react-bootstrap';
import { tags } from "./Tags";


const TagSlide = () => {
    const settings = {
        dots: true,
        infinite: false,
        speed: 500,
        slidesToShow: 6,
        slidesToScroll: 4,
        initialSlide: 0,
        responsive: [
          {
            breakpoint: 1024,
            settings: {
              slidesToShow: 3,
              slidesToScroll: 3,
              infinite: true,
              dots: true
            }
          },
          {
            breakpoint: 600,
            settings: {
              slidesToShow: 2,
              slidesToScroll: 2,
              initialSlide: 2
            }
          },
          {
            breakpoint: 480,
            settings: {
              slidesToShow: 1,
              slidesToScroll: 1
            }
          }
        ]
      };

    return (
        <div className="slider">
            <Slider {...settings}>
                {tags.map(item => (
                    <Card flexfill>
                        <Card.Body style={{ width: '5px' }}>
                            <Button >{item.title}</Button>
                        </Card.Body>
                    </Card>
                ))}
            </Slider>
        </div>
     );
}
 
export default TagSlide;
