  admin_level: number;
  is_suburb: boolean;
  locale_names: {
    default: string[];
  };
  _geoloc: LatLng;
  objectID: string;
  _highlightResult: {
    country: {
      default: ValueMatch;
      [key: string]: ValueMatch;
    };
    city: {
      default: ValueMatch[];
      [key: string]: ValueMatch[];
    };
    postcode: ValueMatch[];
    administrative: ValueMatch[];
    locale_names: {
      default: ValueMatch[];
    };
  };
