export type RequestResult = {
  features: RawResult[];
};
export interface RawResult {
  center: [string, string];
  text: string;
  place_name: string;
  bbox: [string, string, string, string];
}

export type MapBoxProviderOptions = {
  searchUrl?: string;
  reverseUrl?: string;
} & ProviderOptions;

export default class MapBoxProvider extends AbstractProvider<
  RequestResult,
  RawResult
> {
  searchUrl: string;

  constructor(options: MapBoxProviderOptions = {}) {
    super(options);

    const host = 'https://a.tiles.mapbox.com';
    this.searchUrl = options.searchUrl || `${host}/v4/geocode/mapbox.places/`;
  }

  endpoint({ query }: EndpointArgument): string {
    return this.getUrl(`${this.searchUrl}${query}.json`);
  }

  parse(response: ParseArgument<RequestResult>): SearchResult<RawResult>[] {
    const records = Array.isArray(response.data.features)
      ? response.data.features
      : [];

    return records.map((r) => {
      let bounds = null;
      if (r.bbox) {
        bounds = [
          [parseFloat(r.bbox[1]), parseFloat(r.bbox[0])], // s, w
          [parseFloat(r.bbox[3]), parseFloat(r.bbox[2])], // n, e
        ] as BoundsTuple;
      }

      return {
        x: Number(r.center[0]),
        y: Number(r.center[1]),
        label: r.place_name ? r.place_name : r.text,
        bounds,
        raw: r,
      };
    });
  }
}
import AbstractProvider, {
  EndpointArgument,
  ParseArgument,
  ProviderOptions,
  SearchResult,
  RequestType,
} from './provider';

export interface RequestResult {
  features: RawResult[];
  type: string;
  version: string;
  attribution: string;
  licence: string;
  query: string;
  limit: string;
}

export interface RawResult {
  properties: {
    label: string;
    score: number;
    importance: number;
    x: number;
    y: number;
    housenumber: string;
    id: string;
    type: string;
    name: string;
    postcode: string;
    citycode: string;
    city: string;
    context: string;
    street: string;
  };
  type: string;
  geometry: {
    coordinates: number[];
    type: string;
  };
}

export type GeoApiFrProviderOptions = {
  searchUrl?: string;
  reverseUrl?: string;
} & ProviderOptions;

export default class GeoApiFrProvider extends AbstractProvider<
  RequestResult,
  RawResult
> {
  searchUrl: string;
  reverseUrl: string;

  constructor(options: GeoApiFrProviderOptions = {}) {
    super(options);

    const host = 'https://api-adresse.data.gouv.fr';
    this.searchUrl = options.searchUrl || `${host}/search`;
    this.reverseUrl = options.reverseUrl || `${host}/reverse`;
  }

  endpoint({ query, type }: EndpointArgument) {
    const params = typeof query === 'string' ? { q: query } : query;

    switch (type) {
      case RequestType.REVERSE:
        return this.getUrl(this.reverseUrl, params);

      default:
        return this.getUrl(this.searchUrl, params);
    }
  }

  parse(result: ParseArgument<RequestResult>): SearchResult<RawResult>[] {
    return result.data.features.map((r) => ({
      x: r.geometry.coordinates[0],
      y: r.geometry.coordinates[1],
      label: r.properties.label,
      bounds: null,
      raw: r,
    }));
  }
}
