import React from "react";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css"; 
// import "slick-carousel/slick/slick-theme.css";
import {Button} from 'react-bootstrap';
import { tags } from "./Tags";


const TagSlide = () => {
    const settings = {
        dots: false,
        infinite: false,
        speed: 500,
        slidesToShow: 8,
        slidesToScroll: 8,
        // initialSlide: 1,
        responsive: [
          {
            breakpoint: 1024,
            settings: {
              slidesToShow: 4,
              slidesToScroll: 4,
              infinite: false,
              dots: false,
            }
          },
          {
            breakpoint: 767,
            settings: {
              slidesToShow: 3,
              slidesToScroll: 3,
              initialSlide: 2,
              prev: true,
 
              // prevarrow: true,
              // nextArrow: true,
            }
          },
          {
            breakpoint: 480,
            settings: {
              slidesToShow: 1,
              slidesToScroll: 1,
              // prevArrow: true,
              // nextArrow: true,
            }
          }
        ]
      };

    return (
        <div className="slider constainer">
              {/* <span className="prev-arrow"><i className="fas fa-angle-left"></i></span> */}
            <Slider calssName="slick-slider" direction="horizontal" gap="4" {...settings}>
                {tags.map(item => (
                  <Button className="tag-button shadow mb-5" variant="dark" size="sm">{item.title}</Button>
                  ))}
            </Slider>
                {/* <span className="next-arrow"><i className="fas fa-angle-right"></i></span> */}
        </div>
     );
}
 
export default TagSlide;
