import Modal from "react-bootstrap/Modal";

const MyBoxes = ({
    myBoxes,
    handleMyBoxesClose,
    currentUser,
}) => {

    // Delete Box Function:
    const handleDeleteAccount = async () => {
        await fetch(`${process.env.REACT_APP_BACKEND_URL}/box/delete`, {
            method: "DELETE",
            credentials: "include",
            headers: {
                "Content-Type": "application/json; charset=utf-8",
            },
        });
        localStorage.removeItem("token");
        window.location.reload();
    };
    const Boxes = () => {
        return currentUser?.userBoxes.map((item, index) => {
            return (
                <div className="box-form" key={item._id}>
                    <h4>Box {index + 1}</h4>
                    <h6>Box Location:</h6>
                    <div>x: {item.x}</div>
                    <div>y: {item.y}</div>
                    <img src={item.boxImagePath} alt="img" />
                    <img
                        className="box-image"
                        src="Icons/box-500x500.jpg"
                        alt=""
                    />
                    <h6>Box Items:</h6>
                    <p>Tag1 Tag2 Tag3</p>
                    <button 
                        type="button" className="btn btn-danger btn-sm"
                        
                    >
                        Delete Box
                    </button>
                    <hr />
                </div>
            );
        });
    };

    return (
        <Modal
            show={myBoxes}
            onHide={handleMyBoxesClose}
            backdrop="true"
            fade="true"
            centered="true"
        >
            <Modal.Header closeButton>
                <Modal.Title>My Boxes</Modal.Title>
            </Modal.Header>
            <Modal.Body>{Boxes()}</Modal.Body>
        </Modal>
    );
};

export default MyBoxes;
