import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";
import { SearchControl, OpenStreetMapProvider } from "leaflet-geosearch";
// import '../../node_modules/example/react-leaflet-geosearch.css'
// import GeoSearch from "./GeoSearch";
function Karte() {
  // LEAFLET Data
  // Längengradkoordinate muss zwischen -180 und 180 liegen
  const latitude = 52.45727522348639;
  //  Breitengradkoordinate muss zwischen -90 und 90 liegen
  const longitude = 13.540082992085056;
  // WBS CODING SCHOOL as Marker
  const latitudeStart = 52.45727522348639;
  const longitudeStart = 13.540082992085056;

  const prov = OpenStreetMapProvider();
  const GeoSearchControlElement = SearchControl;


  return (
    <MapContainer
    center={[latitude, longitude]}
    zoom={13}
    scrollWheelZoom={true}
    zoomControl={false} // no +/-  Button to zoom
    >
      <TileLayer
        attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        />
        <GeoSearchControlElement
          provider={prov}
          showMarker={true}
          showPopup={false}
          maxMarkers={3}
          retainZoomLevel={false}
          animateZoom={true}
          autoClose={false}
          searchLabel={"Enter address, please"}
          keepResult={true}
          popupFormat={({ query, result }) => result.label}
        />
      {/* <GeoSearch/> */}
      <Marker position={[latitudeStart, longitudeStart]}>
        <Popup>WBS Coding School !</Popup>
      </Marker>
    </MapContainer>
  );
}
export default Karte;
