import { MapContainer, TileLayer, useMap, Marker, Popup } from "react-leaflet";
import React, {useEffect, useRef} from "react";
import {geosearch} from 'esri-leaflet-geocoder';
// import './App.css';
import 'leaflet/dist/leaflet.css';
import 'esri-leaflet-geocoder/dist/esri-leaflet-geocoder.css';

// const defaultCenter = [0, 0];
// const defaultElement = 4;


function Karte() {

  const [map, setMap] = use
  const mapRef = useRef();
  useEffect(() => {
    const {current = {}} = mapRef;
    console.log(current);
    // const {leafletElement: map} = current;
    if (!map) return;

    const control= geosearch();
    control.addTo(map);
    control.on('results', handleOnSearchResults);
    
    return () => {
      control.off('results', handleOnSearchResults);
    }
  }, []);


  /**
   * handleOnSearchResuts
   * @param {object} data Results object from esri-leaflet-geocoder
   */
   function handleOnSearchResults(data) {
    console.log('Search results', data);
  }


  // LEAFLET Data
  // Längengradkoordinate muss zwischen -180 und 180 liegen
  const latitude = 52.45727522348639;
  //  Breitengradkoordinate muss zwischen -90 und 90 liegen
  const longitude = 13.540082992085056;
  // WBS CODING SCHOOL as Marker
  const latitudeStart = 52.45727522348639;
  const longitudeStart = 13.540082992085056;
  return (
    <MapContainer
      ref= {setMap}
      center={[latitude, longitude]}
      zoom={13}
      scrollWheelZoom={true}
      zoomControl={false} // no +/-  Button to zoom
    >
      <TileLayer
        attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
      />
      <Marker position={[latitudeStart, longitudeStart]}>
        <Popup>WBS Coding School !</Popup>
      </Marker>
    </MapContainer>
  );
}
export default Karte;
