import Modal from "react-bootstrap/Modal";

const UserSettings = ({
    userSettings,
    handleUserSettingsClose,
    currentUser,
}) => {

    // Delete Account Function:
    const handleDeleteAccount = async () => {
        const response = await fetch(
            "https://morning-shelf-75082.herokuapp.com/box/delete",
            {
                method: "DELETE",
                headers: {
                    "Content-Type": "application/json; charset=utf-8",
                },
                body: JSON.stringify()
            }
        )

    };

    return (

        



        <Modal
            show={userSettings}
            onHide={handleUserSettingsClose}
            backdrop="true"
            fade="true"
            centered="true"
        >
            <Modal.Header closeButton>
                <Modal.Title>
                    <h4>User Settings</h4>
                </Modal.Title>
            </Modal.Header>
            <Modal.Body>
                <h6>Username: {currentUser?.userName}</h6>
                <h6>City: {currentUser?.userLocation}</h6>
                <h6>Delete your account:</h6>{" "}
                <button type="button" className="btn btn-danger btn-sm" onClick={handleDeleteAccount}>
                    delete
                </button>
            </Modal.Body>
        </Modal>
    );
};

export default UserSettings;
