import Container from "react-bootstrap/Container";
import Nav from "react-bootstrap/Nav";
import Navbar from "react-bootstrap/Navbar";
import React, { useState } from "react";

import Signup from "./navbar-components/Signup";
import AddNewBox from "./navbar-components/AddNewBox";
import Login from "./navbar-components/Login";
import UserSettings from "./navbar-components/UserSettings";
import MobileView from "./navbar-components/MobileView";

const CustomNavbar = () => {
    // add a new box
    const [showAddNewBox, setShowAddNewBox] = useState(false);
    const handleClose = () => setShowAddNewBox(false);
    const handleShow = () => setShowAddNewBox(true);
    // User settings
    const [userSettings, setUserSettings] = useState(false);
    const handleUserSettingsClose = () => setUserSettings(false);
    const handleUserSettings = () => setUserSettings(true);
    // Sign up
    const [userSignup, setUserSignup] = useState(false);
    const handleUserSignupClose = () => setUserSignup(false);
    const handleUserSignup = () => setUserSignup(true);
    // log in
    const [showUserLogin, setShowUserLogin] = useState(false);
    const handleUserLoginClose = () => setShowUserLogin(false);
    const handleUserLogin = () => setShowUserLogin(true);

    const [currentUser, setCurrentUser] = useState({});
    console.log(currentUser);

    return (
        <header>
            <Navbar
                bg="dark"
                variant="dark"
                expand=""
                fixed="top"
                className="bootstrap_navbar"
            >
                <Container fluid space-around>
                    <Navbar.Brand href="#">
                        BOX <i className="bx bx-package" />
                    </Navbar.Brand>
                    <Navbar.Toggle aria-controls="basic-navbar-nav" />
                    <Navbar.Collapse id="basic-navbar-nav">
                        <Nav className="me-auto">
                            {Object.hasOwn(currentUser, "_id") ? null : (
                                <Nav.Link href="#" onClick={handleUserSignup}>
                                    Sign up
                                </Nav.Link>
                            )}
                            <Nav.Link href="#" onClick={handleUserLogin}>
                                {Object.hasOwn(currentUser, "_id")
                                    ? "Log Out"
                                    : "Log In"}
                            </Nav.Link>
                            {Object.hasOwn(currentUser, "_id") && (
                                <>
                                    <Nav.Link href="#" onClick={handleShow}>
                                        Add a new box
                                    </Nav.Link>
                                    <Nav.Link
                                        href="#"
                                        onClick={handleUserSettings}
                                    >
                                        User
                                    </Nav.Link>
                                </>
                            )}
                        </Nav>
                    </Navbar.Collapse>
                </Container>
            </Navbar>

            <Signup
                userSignup={userSignup}
                handleUserSignupClose={handleUserSignupClose}
            />

            <Login
                showUserLogin={showUserLogin}
                handleUserLoginClose={handleUserLoginClose}
                setCurrentUser={setCurrentUser}
            />

            <AddNewBox
                showAddNewBox={showAddNewBox}
                handleClose={handleClose}
                currentUser={currentUser}
            />

            <UserSettings
                userSettings={userSettings}
                handleUserSettingsClose={handleUserSettingsClose}
                currentUser={currentUser}
            />

            <MobileView
                handleUserSettings={handleUserSettings}
                handleShow={handleShow}
            />
        </header>
    );
};
export default CustomNavbar;
